defmodule Aoc2017Day01Test do
  use ExUnit.Case

  test "check_pairs returns zero when the arguments are different" do
    assert Aoc2017Day01.check_pairs([0, 1]) == 0
  end

  test "check_pairs returns 3 when the arguments are 3" do
    assert Aoc2017Day01.check_pairs([3, 3]) == 3
  end

  test "First test for Task 1" do
    assert Aoc2017Day01.task1("1122") == 3
  end

  test "Second test for Task 1" do
    assert Aoc2017Day01.task1("1111") == 4
  end

  test "Third test for Task 1" do
    assert Aoc2017Day01.task1("1234") == 0
  end

  test "Fourth test for Task 1" do
    assert Aoc2017Day01.task1("91212129") == 9
  end

  test "My AoC input for task 1" do
    input =
      ""

    assert Aoc2017Day01.task1(input) == 1341
  end

  test "First test for Task 2" do
    assert Aoc2017Day01.task2("1212") == 6
  end

  test "Second test for Task 2" do
    assert Aoc2017Day01.task2("1221") == 0
  end

  test "Third test for Task 2" do
    assert Aoc2017Day01.task2("123425") == 4
  end

  test "Fourth test for Task 2" do
    assert Aoc2017Day01.task2("123123") == 12
  end

  test "Fifth test for Task 2" do
    assert Aoc2017Day01.task2("12131415") == 4
  end

  test "My AoC input for task 2" do
    input =
      ""

    assert Aoc2017Day01.task2(input) == 1348
  end
end
